hotspot.onclick:
set(enabled,false);
			              set(view.maxpixelzoom,null);
			              set(view.fovmin,28);
			              lookto(10.7, 3.5, 42);
			              loadscene(scene_vorraum, null, MERGE, BLEND(2));
			              lookat(-94, 19, 97);
			              wait(LOAD);
			              oninterrupt(break);
			              wait(BLEND);
			              lookto(-352.7, -1.6, 108, smooth(45,45,60), false);

